(function(){const footer=document.querySelector('.site-footer');const newsletterForm=document.querySelector('.newsletter-form');if(!footer||!newsletterForm)return;let hasAnimated=false;const observer=new IntersectionObserver((entries)=>{entries.forEach(entry=>{if(entry.isIntersecting&&!hasAnimated){hasAnimated=true;newsletterForm.classList.add('animate-border');setTimeout(()=>{newsletterForm.classList.remove('animate-border');},5000);observer.disconnect();}});},{threshold:0.3});observer.observe(footer);})();
